% -*- mode: prolog -*-

go(Pos, [], Pos).
go(Pos, [forward(D)|Cs], X-Y) :- go(Pos, Cs, X0-Y), X is X0 + D.
go(Pos, [down(D)|Cs], X-Y) :- go(Pos, Cs, X-Y0), Y is Y0 + D.
go(Pos, [up(D)|Cs], X-Y) :- go(Pos, Cs, X-Y0), Y is Y0 - D.

adv02(X) :- course(Cs), go(0-0, Cs, H-V), X is H * V.

aim(Pos, [], Pos).
aim(pos(X,Y,A), [forward(D)|Cs], Pos) :-
    X1 is X + D, Y1 is Y + A * D,
    aim(pos(X1,Y1,A), Cs, Pos).
aim(pos(X,Y,A), [down(D)|Cs], Pos) :- A1 is A + D, aim(pos(X,Y,A1), Cs, Pos).
aim(pos(X,Y,A), [up(D)|Cs], Pos) :- A1 is A - D, aim(pos(X,Y,A1), Cs, Pos).

adv02b(X) :- course(Cs), aim(pos(0,0,0), Cs, pos(H,V,_)), X is H * V.

