function init(img)
    n = length(img)
    arr = fill(false, (n, n))
    for i in 1:n, j in 1:n
        arr[i,j] = img[i][j] == '#'
    end
    arr
end

function compute_index(def, arr, x, y)
    acc = 0
    for i in -1:1, j in -1:1
        if x+i < 1 || x+i > size(arr, 1) || y+j < 1 || y+j > size(arr, 2)
            acc = 2 * acc + def
        else
            acc = 2 * acc + arr[x+i,y+j]
        end
    end
    acc
end

function step(def, str, arr)
    n = size(arr, 1)
    result = fill(!def, (n + 2, n + 2))
    for i in 1:n+2, j in 1:n+2
        index = compute_index(def, arr, i - 1, j - 1)
        result[i,j] = str[index+1] == '#'
    end
    result
end

function enhance(str, n, img)
    arr = init(img)
    for i in 1:n
        arr = step(i % 2 == 0, str, arr)
    end
    arr
end

